<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Acreditar extends RR_Controller {    public function Acreditar() {        parent::__construct();        $this->load->model("acreditar_mod", "Acreditar");        $this->load->model("eventos_mod", "Evento");        $this->widget_view = array('validate' => array('js' => array('jquery.validate', 'messages_es'))        );    }    public function index() {        $this->extra_title = 'AcreditaciÃ³n';        $data = array('action'      => lang_url('acreditar/search'),                      'acreditados' => $this->Acreditar->getList(),                      'evento'      => $this->Evento->getEventoActive(),        );        $module = $this->view('acreditar/index', $data);                $this->_show($module);    }    public function detalle(){        $this->extra_title = 'Detalle del participante';        $data = array('acreditado'=>$this->Acreditar->getById($this->params['id']));        $module = $this->view('acreditar/detalle', $data);                $this->_show($module);    }    public function search() {        $search = $this->Acreditar->getByKey();        echo json_encode($search);    }    public function alta() {        $acreditar = $this->Acreditar->alta();        echo json_encode($acreditar);    }    private function _show($module) {        echo $this->show_main($module);    }}